import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles, withStyles, useTheme } from '@material-ui/core/styles';
import clsx from 'clsx';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Check from '@material-ui/icons/Check';
import SettingsIcon from '@material-ui/icons/Settings';
import GroupAddIcon from '@material-ui/icons/GroupAdd';
import VideoLabelIcon from '@material-ui/icons/VideoLabel';
import StepConnector from '@material-ui/core/StepConnector';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Head from 'next/head'
import Header from "components/Header/Header.js";
import HeaderLinks from "components/Header/HeaderLinks.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";

import Grow from '@material-ui/core/Grow';

import MobileStepper from '@material-ui/core/MobileStepper';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';

import SectionFooter from "pages-sections/footer/SectionFooter.js";
import SectionContacts from "pages-sections/checkout/SectionContacts.js";

import SectionIdentification from "pages-sections/checkout/SectionIdentification.js";
import SectionPayment from "pages-sections/checkout/SectionPayment.js";
import SectionAnamnese from "pages-sections/checkout/SectionAnamnese.js";
import SectionFinish from "pages-sections/checkout/SectionFinish.js";

import logo from "assets/img/logo.png";





const QontoConnector = withStyles({
  alternativeLabel: {
    top: 10,
    left: 'calc(-50% + 16px)',
    right: 'calc(50% + 16px)',
  },
  active: {
    '& $line': {
      borderColor: '#784af4',
    },
  },
  completed: {
    '& $line': {
      borderColor: '#784af4',
    },
  },
  line: {
    borderColor: '#eaeaf0',
    borderTopWidth: 3,
    borderRadius: 1,
  },
})(StepConnector);

const useQontoStepIconStyles = makeStyles({
  root: {
    color: '#eaeaf0',
    display: 'flex',
    height: 22,
    alignItems: 'center'     
  },
  active: {
    color: '#784af4',
  },
  circle: {
    width: 8,
    height: 8,
    borderRadius: '50%',
    backgroundColor: 'currentColor',
  },
  completed: {
    color: '#784af4',
    zIndex: 1,
    fontSize: 18,
  },
});

function QontoStepIcon(props) {
  const classes = useQontoStepIconStyles();
  const { active, completed } = props;

  return (
    <div
      className={clsx(classes.root, {
        [classes.active]: active,
      })}
    >
      {completed ? <Check className={classes.completed} /> : <div className={classes.circle} />}
    </div>
  );
}

QontoStepIcon.propTypes = {
  /**
   * Whether this step is active.
   */
  active: PropTypes.bool,
  /**
   * Mark the step as completed. Is passed to child components.
   */
  completed: PropTypes.bool,
};

const ColorlibConnector = withStyles({
  alternativeLabel: {
    top: 22,
  },
  active: {
    '& $line': {
      backgroundImage:
        'linear-gradient(90deg, rgba(66,92,199,1) 0%, rgba(59,191,173,1) 100%);',
    },
  },
  completed: {
    '& $line': {
      backgroundImage:
        'linear-gradient(90deg, rgba(66,92,199,1) 0%, rgba(59,191,173,1) 100%);',
    },
  },
  line: {
    height: 3,
    border: 0,
    backgroundColor: '#eaeaf0',
    borderRadius: 1,
  },
})(StepConnector);

const useColorlibStepIconStyles = makeStyles({
  root: {
    backgroundColor: '#ccc',
    zIndex: 1,
    color: '#fff',
    width: 50,
    height: 50,
    display: 'flex',    
    borderRadius: '50%',
    justifyContent: 'center',
    alignItems: 'center',
  },  
  active: {
    backgroundImage:
      'linear-gradient(90deg, rgba(66,92,199,1) 0%, rgba(59,191,173,1) 100%)',
    boxShadow: '0 4px 10px 0 rgba(0,0,0,.25)',
  },
  completed: {
    backgroundImage:
      'linear-gradient(90deg, rgba(66,92,199,1) 0%, rgba(59,191,173,1) 100%);',
  }  
});

function ColorlibStepIcon(props) {
  const classes = useColorlibStepIconStyles();
  const { active, completed } = props;

  const icons = {
    1: <SettingsIcon />,
    2: <GroupAddIcon />,
    3: <VideoLabelIcon />,
    4: <VideoLabelIcon />,
  };

  return (
    <div
      className={clsx(classes.root, {
        [classes.active]: active,
        [classes.completed]: completed,
      })}
    >
      {icons[String(props.icon)]}
    </div>
  );
}

ColorlibStepIcon.propTypes = {
  /**
   * Whether this step is active.
   */
  active: PropTypes.bool,
  /**
   * Mark the step as completed. Is passed to child components.
   */
  completed: PropTypes.bool,
  /**
   * The label displayed in the step icon.
   */
  icon: PropTypes.node,
};

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    backgroundColor: "#ffffff"
  },
  button: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  }, 
  progress: {
    // display: "none
    margin: "0 auto"
  },
  textCenter: {
    textAlign: "center"
  },
  imgLogo: {
    maxHeight: "90px",
    margin: "0 auto"
  },
  containerLogo: {
    // textAlign: "center",
    display: "flex",
    alignItems: "center"
  },
  containerStep: {
    padding: "8px"
  },
  containerTopo: {
    padding: 0,
    margin: 0
  }
}));

function getSteps() {
  return ['Identificação', 'Pagamento', 'Anamnese', "Finalização"];
}



export default function CustomizedSteppers() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const [activeAnamnese, setActiveAnamnese] = React.useState(0);
  const steps = getSteps();
  const theme = useTheme();

  const handleNext = (type) => {
    switch (type) {
      case "Checkout":
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
        return
      case "Anamnese":        
        if (activeAnamnese === 4){
          setActiveStep((prevActiveStep) => prevActiveStep + 1);   
        } else {
          setActiveAnamnese((prevActiveStep) => prevActiveStep + 1);   
        }  
        return
      default:
        return 
    }    
  };

  const handleBack = (type) => {
    switch (type) {
      case "Checkout":
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
        return
      case "Anamnese":
        if (activeAnamnese === 0){
          setActiveStep((prevActiveStep) => prevActiveStep - 1);   
        } else {
          setActiveAnamnese((prevActiveStep) => prevActiveStep - 1);   
        }
        return
      default:
        return 
    }    
  };

  const handleReset = () => {
    setActiveStep(0);  
    setActiveAnamnese(0);      
  };

  const  getStepContent = (step) => {
    switch (step) {
      case 0:
        return <SectionIdentification/>;
      case 1:
        return <SectionPayment/>;
      case 2:
        return <SectionAnamnese/>
      case 3:
        return <SectionFinish/>
      default:
        return 'Unknown step';
    }
  }

  return (
    <>

      <Head>
          <title>Checkout - Clicou Personal</title>
          {/* <meta name="viewport" content="initial-scale=1.0, width=device-width" /> */}
      </Head>
      {/* <div>
        <Head>
          <title>Checkout - Clicou Personal</title>
          <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        </Head>
        <Header          
          color="primary"
          // brand="NextJS Material Kit PRO"
          // imgBrand={image}
          links={<HeaderLinks dropdownHoverColor="info" />}
        />
      </div> */}
      <div className={classes.root}>   

      <GridContainer justify="center" className={classes.containerTopo}> 
        <GridItem xs={12} sm={12} md={2} className={classes.containerLogo}>
          <img src={logo} alt="..." className={classes.imgLogo} />
        </GridItem> 
        <GridItem xs={12} sm={12} md={10}>
          <Stepper alternativeLabel activeStep={activeStep} connector={<ColorlibConnector />} className={classes.containerStep}>
            {steps.map((label) => (
              <Step key={label}>              
                <StepLabel StepIconComponent={ColorlibStepIcon}>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

        </GridItem> 
      </GridContainer>


        <div className={classes.textCenter}>
          {activeStep === steps.length ? (
            <div>
              <Typography className={classes.instructions}>
                All steps completed - you&apos;re finished
              </Typography>
              <Button onClick={handleReset} className={classes.button}>
                Reset
              </Button>
            </div>
          ) : (
            <>
              { activeStep === 10 ? (

                <div>                  
                  {getStepContent(activeStep)}                
                  <div>
                    <Button disabled={activeStep === 0} onClick={() => handleBack("Anamnese")} className={classes.button}>
                      Back
                    </Button>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => handleNext("Anamnese")}
                      className={classes.button}
                    >
                      {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                    </Button>
                  </div>
                </div>

              ) : (

                <div>

                  <Grow 
                    in={true}
                    style={{ transformOrigin: '0 0 0' }}
                    {...(true ? { timeout: 1000 } : {})}
                  >                    
                    {getStepContent(activeStep)}                    
                  </Grow>           
                  <div>
                    <Button disabled={activeStep === 0} onClick={() => handleBack("Checkout")} className={classes.button}>
                      Back
                    </Button>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => handleNext("Checkout")}
                      className={classes.button}
                    >
                      {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                    </Button>
                  </div>
                </div>

              )}
            </>

          )}
        </div>


        { activeStep === 2 && (
          <MobileStepper
            variant="progress"
            steps={5}
            position="static"
            activeStep={activeAnamnese}
            // className={classes.root}
            classes={{
              root: classes.root,          
              progress: classes.progress,
            }}
          />
        )}


        <SectionFooter/>

      </div>
    </>
  );
}
